#!/bin/env/pythonimport numpy as npprint("Question a")a = np.zeros(10)a[5] = 1print(a)print("Question b")b = np.arange(10,50,1)print("Question c")c = b[::-1]print(c)print("Question d")d = np.arange(0,9).reshape(3,3)print(d)print("Question e")e = np.array([1,2,0,0,4,0])e = e[e != 0]print(e)print("Question f")f = np.random.randint(0,30,size=(30))print(f)print(f.mean())print("Question g")g = np.zeros(20).reshape(5,4)g[0:1,:] = 1g[:,0:1] = 1g[-1:,:] = 1g[:,-1:] = 1print(g)print("Question h")h = np.array([[0,1]*4,[1,0]*4]*4)print(h)print("Question i")i = np.array([[0,1],[1,0]])i = np.tile(i, (4,4))print(i)print("Question j")j = np.arange(11)j = j[(j > 3) & (j < 8)]print(j)print("Question k")k = np.random.random(10)k.sort()print(k)print("Question l")A = np.random.randint(0,2,5)B = np.random.randint(0,2,5)print(A == B)print("Question m")m = np.arange(10, dtype=np.int32)print(m.dtype)m = m.view('float32')print(m.dtype)print("Question n")A = np.arange(9).reshape(3,3)B = A + 1C = np.dot(A,B)D = C.diagonal()print(D)